import './App.css'
import { useState } from 'react';


import Home from './components/Home';
import Counter from './components/Counter';


function App() {
  const [State,setState]=useState(false)



  return (
    <div>
      <div className="App">
        <h1 onClick={()=>setState(!State)}>Hide/show</h1>
        { State ? <Counter />:null }
      </div>




    </div>
  );
}

export default App;
